import React, {useState}  from "react";
import Axios from "axios";

function Postform(){
    const url = "http://0.0.0.0:8000/vocab"
    const [data, setData] = useState({
        vocab: "",

    })
    function sumit(e){
        e.preventDefault();
        Axios.post(url,{
            vocab: data.vocab
        }).then(res=>{console.log(res.data)})
    }
    function handle(e){
        const newData = {...data}
        newData[e.target.id] = e.target.value
        setData(newData)
        console.log(newData)
    }
    return(
        <div>
            <form onSubmit={(e) => sumit(e)}>
                <input id="input_vocab" onChange={(e) => handle(e)} value={data.vocab} type="text">

                </input>
                <Submit>Submit</Submit>
            </form>
        </div>
    )
}
export default Postform;
